import React, { useState } from "react";
import './searchBar.css'
import { useSelector, useDispatch } from "react-redux";
import { setSearchState } from "../../state/storeSlice/searchCardSlice";
import arrow from "../../ImageForComponents/Arrow.svg"
import {setSearchCountryState} from "../../state/storeSlice/CountrySlice" 


const SearchBar = () =>{
    const [tempSrtingValue,setTempString] = useState("")
    const dispatch = useDispatch()
    const country = useSelector((state) => state.country.mode)
    return(
        <div className="wrapperSearch">
            <div className="containerSearch">
                <div className="containerForCountry">
                    <img src={arrow} className="imageCountry"/>
                    <input className="inputCss" placeholder="Место" type="text" value={tempSrtingValue} onChange={(event) => setTempString(event.target.value)}/>
                </div>
                <input className="inputCss" placeholder="Ввод" type="text" onChange={(event)=>{
                    dispatch(setSearchState(event.target.value))
                }} />



                <div className="ButtonSearch" onClick={(event) =>{ dispatch(setSearchCountryState(tempSrtingValue))}}>
                    <p className="textSearch">поиск</p>    
                </div>              
            </div>
        </div>
    )
}

export default SearchBar
