import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  mode: "",
};

const searchUserModeSlice = createSlice({
  name: "searchUserMode",
  initialState,
  reducers: {
    setSearchState(state,action){
        state.mode = action.payload 
    }
  },
});

export const { setSearchState } = searchUserModeSlice.actions;
export default searchUserModeSlice.reducer;
