import React from "react";
import Card from "../Card/card";
import "./cardList.css"
import { useSelector } from "react-redux";



const getFilterCard = (cards,value,tagsState,countryState) =>{
    let massCards = cards
    
    if(value){
        massCards = massCards.filter((item) => item.jobName.toLowerCase().includes(value.toLowerCase()));
    }
    if(countryState){
        massCards = massCards.filter((item) => item.counName.toLowerCase().includes(countryState.toLowerCase()));
    }
    if(!tagsState.length)
        return massCards
    return massCards.filter(
        (item) => item.tags.filter((tag) => tagsState.includes(tag)).length === tagsState.length
    )

}

const getCorrectCards = (cards,headerState,value,tagsState,countryState) =>{
        return getFilterCard(headerState === "Search" ? cards : cards.filter(item => item.isLiked === true),value,tagsState,countryState)
}

const CardList = () =>{
    const tagsState = useSelector((state) => state.tags.mode)
    const headerState = useSelector((state) => state.searchMode.mode);
    const massItems = useSelector((state) => state.setMassMode.mode);
    const searchString = useSelector((state) => state.searchUserMode.mode)
    const countryState = useSelector((state) => state.country.mode)
    return(
    <div className="wrapperList">
        <div className="containerList">
            { getCorrectCards(massItems,headerState,searchString,tagsState,countryState).map((item,index)=>{
                return <Card  key={index} card={item}  />
            })}

            
        </div>
    </div>
    )
}
export default CardList
