import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  mode: "",
};

const CountrySlice = createSlice({
  name: "countryMode",
  initialState,
  reducers: {
    setSearchCountryState(state,action){
        state.mode = action.payload 
    }
  },
});

export const { setSearchCountryState } = CountrySlice.actions;
export default CountrySlice.reducer;
